<?php$email = '';$password = '';function login(){    global $email;    $email = $_COOKIE["email"];    global $password;    $password = $_COOKIE["pass"];    if (strlen($_GET['email']) == 0) {        echo json_encode(array("status" => false, "err" => "please enter an email address."));    } else if ($_GET['email'] == $email && $_GET['password'] == $password) {        echo json_encode(array("status" => true, "msg" => "login is done successfully."));    } else {        echo json_encode(array("status" => false, "err" => "email or password is wrong"));    }}function signUp(){    global $email;    global $password;    if (strlen($_POST['fullname']) < 3) {        echo json_encode(array("error" => true, "text" => "fullname is not valid."));    }else{        if (strlen($_POST['email']) < 5) {            echo json_encode(array("error" => true, "text" => "email is not valid."));        } else {            if (strlen($_POST['password']) < 5) {                echo json_encode(array("error" => true, "text" => "password is not valid."));            }            else{            if ($_POST['password'] != $_POST['password2']) {            echo json_encode(array("error"=> true , "text" => "passwords are not the same."));            } else {                $email = $_POST['email'];                $password = $_POST['password'];                echo json_encode(array("status" => true, "text" => "successful", ));                setcookie("email" , $email , time()+ (20*365*24*60*60),"/");                setcookie("pass" , $password ,time()+ (20*365*24*60*60), "/");            }}                    }            }}    if (!empty($_GET['email'])) {        login();    } else if (!empty($_POST['do_signup'])) {        signUp();    }